import React, { createContext, useState, useContext, ReactNode, useRef, useCallback, useEffect } from 'react';
import { useAuth } from './useAuth';
import { supabase } from '../supabaseClient';
import { RealtimeChannel } from '@supabase/supabase-js';

const iceServers = {
  iceServers: [
    { urls: 'stun:stun.l.google.com:19302' },
    { urls: 'stun:stun1.l.google.com:19302' },
    { urls: 'stun:stun2.l.google.com:19302' },
    { urls: 'stun:stun3.l.google.com:19302' },
    { urls: 'stun:stun4.l.google.com:19302' },
    { urls: 'stun:stun.services.mozilla.com' },
  ],
};

interface CallContextType {
    isCalling: boolean;
    callee: string;
    callStatus: string;
    isMuted: boolean;
    isVideoEnabled: boolean;
    incomingCall: { from: string; isVideoCall: boolean; } | null;
    localStream: MediaStream | null;
    remoteStream: MediaStream | null;
    callDuration: number;
    startP2PCall: (callee: string, withVideo: boolean) => void;
    acceptCall: () => void;
    declineCall: () => void;
    endCall: (targetOverride?: string) => void;
    toggleMute: () => void;
    toggleVideo: () => void;
}

const CallContext = createContext<CallContextType | undefined>(undefined);

type SoundEffect = 'ring' | 'disconnected' | 'declined';

// Base64 encoded audio files to prevent network/CORS issues and ensure reliability.
const SOUND_DATA_URIS: Record<SoundEffect, string> = {
    ring: 'data:audio/mp3;base64, Lavf58.29.---AACF',
    disconnected: 'data:audio/mp3;base64, Lavf58.29.---AACg',
    declined: 'data:audio/mp3;base64, Lavf58.29.---AACg'
};

export const CallProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
    const { user } = useAuth();
    const [isCalling, setIsCalling] = useState(false);
    const [callee, setCallee] = useState('');
    const [callStatus, setCallStatus] = useState('');
    const [isMuted, setIsMuted] = useState(false);
    const [isVideoEnabled, setIsVideoEnabled] = useState(true);
    const [incomingCall, setIncomingCall] = useState<{ from: string; isVideoCall: boolean; } | null>(null);
    const [localStream, setLocalStream] = useState<MediaStream | null>(null);
    const [remoteStream, setRemoteStream] = useState<MediaStream | null>(null);
    const [callDuration, setCallDuration] = useState(0);

    const pc = useRef<RTCPeerConnection | null>(null);
    const userChannel = useRef<RealtimeChannel | null>(null);
    const callTimeoutRef = useRef<number | null>(null);
    const callDurationIntervalRef = useRef<number | null>(null);
    const audioContextRef = useRef<AudioContext | null>(null);
    const audioBuffersRef = useRef<Record<SoundEffect, AudioBuffer | null>>({ ring: null, disconnected: null, declined: null });
    const soundSourceRef = useRef<AudioBufferSourceNode | null>(null);

    // --- Audio Engine ---
    const initAudio = useCallback(async () => {
        if (audioContextRef.current && audioContextRef.current.state === 'running') return true;
        
        try {
            const AudioContext = window.AudioContext || (window as any).webkitAudioContext;
            if(!AudioContext) throw new Error("AudioContext not supported");
            
            const context = new AudioContext();
            audioContextRef.current = context;

            // Unlock audio context on any user interaction
            const unlock = () => {
                if (context.state === 'suspended') {
                    context.resume();
                }
                document.body.removeEventListener('click', unlock);
                document.body.removeEventListener('touchstart', unlock);
            };
            document.body.addEventListener('click', unlock);
            document.body.addEventListener('touchstart', unlock);
            
            // Decode all sounds
            for (const key in SOUND_DATA_URIS) {
                const sound = key as SoundEffect;
                const response = await fetch(SOUND_DATA_URIS[sound]);
                const arrayBuffer = await response.arrayBuffer();
                audioBuffersRef.current[sound] = await context.decodeAudioData(arrayBuffer);
            }
            return true;
        } catch (e) {
            console.error("Failed to initialize audio for calls:", e);
            return false;
        }
    }, []);

    const playSound = (sound: SoundEffect, loop = false) => {
        stopSound(); // Stop any currently playing sound
        if (!audioContextRef.current || !audioBuffersRef.current[sound]) return;
        if (audioContextRef.current.state === 'suspended') audioContextRef.current.resume();

        const source = audioContextRef.current.createBufferSource();
        source.buffer = audioBuffersRef.current[sound]!;
        source.loop = loop;
        source.connect(audioContextRef.current.destination);
        source.start();
        soundSourceRef.current = source;
    };

    const stopSound = () => {
        if (soundSourceRef.current) {
            soundSourceRef.current.stop();
            soundSourceRef.current = null;
        }
    };

    const endCall = useCallback((targetOverride?: string) => {
        const target = targetOverride || callee;

        if (target) {
            const channel = supabase.channel(`user:${target}`);
            channel.send({
                type: 'broadcast',
                event: 'call-ended',
                payload: { from: user?.username },
            });
            supabase.removeChannel(channel);
        }

        if (pc.current) {
            pc.current.close();
            pc.current = null;
        }
        if (localStream) {
            localStream.getTracks().forEach(track => track.stop());
            setLocalStream(null);
        }
        if (callTimeoutRef.current) {
            // FIX: Use window.clearTimeout to avoid type conflict with Node.js's Timeout type.
            window.clearTimeout(callTimeoutRef.current);
            callTimeoutRef.current = null;
        }
        if (callDurationIntervalRef.current) {
            // FIX: Use window.clearInterval to avoid type conflict with Node.js's Timeout type.
            window.clearInterval(callDurationIntervalRef.current);
            callDurationIntervalRef.current = null;
        }

        stopSound();
        setRemoteStream(null);
        setIsCalling(false);
        setCallee('');
        setCallStatus('');
        setCallDuration(0);
        setIncomingCall(null);
    }, [callee, localStream, user?.username]);

    const createPeerConnection = useCallback((calleeUsername: string) => {
        const peerConnection = new RTCPeerConnection(iceServers);
        pc.current = peerConnection;

        localStream?.getTracks().forEach(track => {
            pc.current?.addTrack(track, localStream);
        });

        pc.current.onicecandidate = event => {
            if (event.candidate) {
                const channel = supabase.channel(`user:${calleeUsername}`);
                channel.send({
                    type: 'broadcast',
                    event: 'ice-candidate',
                    payload: { candidate: event.candidate, from: user?.username },
                });
                supabase.removeChannel(channel);
            }
        };

        pc.current.ontrack = event => {
            setRemoteStream(event.streams[0]);
        };

        pc.current.onconnectionstatechange = () => {
            if (pc.current?.connectionState === 'connected') {
                setCallStatus('Connected');
                stopSound();
                if (callDurationIntervalRef.current) window.clearInterval(callDurationIntervalRef.current);
                // FIX: Use window.setInterval to get a number return type, avoiding conflict with Node.js's Timeout type.
                callDurationIntervalRef.current = window.setInterval(() => setCallDuration(d => d + 1), 1000);
            } else if (['disconnected', 'failed', 'closed'].includes(pc.current?.connectionState || '')) {
                endCall();
            }
        };

        return peerConnection;
    }, [localStream, user?.username, endCall]);

    const startP2PCall = useCallback(async (calleeUsername: string, withVideo: boolean) => {
        setIsCalling(true);
        setCallStatus('Calling...');
        try {
            if (!user) throw new Error("User not logged in.");
            await initAudio(); // Ensure audio is ready
            
            const stream = await navigator.mediaDevices.getUserMedia({
                video: withVideo,
                audio: true,
            });
            setLocalStream(stream);
            setCallee(calleeUsername);
            setIsVideoEnabled(withVideo);

            const peerConnection = createPeerConnection(calleeUsername);
            const offer = await peerConnection.createOffer();
            await peerConnection.setLocalDescription(offer);

            // Save the offer to DB before sending the broadcast
            await supabase.from('webrtc_offers').upsert({ caller: user.username, callee: calleeUsername, offer: offer });

            const channel = supabase.channel(`user:${calleeUsername}`);
            channel.send({
                type: 'broadcast',
                event: 'incoming-call',
                payload: { from: user.username, isVideoCall: withVideo },
            });
            supabase.removeChannel(channel);

            setCallStatus('Ringing...');
            
            // Set a timeout for the call attempt
            // FIX: Use window.setTimeout to get a number return type, avoiding conflict with Node.js's Timeout type.
            callTimeoutRef.current = window.setTimeout(() => {
                if(isCalling && callStatus.startsWith('Ringing')) {
                    playSound('disconnected');
                    setCallStatus('User unavailable');
                    // FIX: Use window.setTimeout to avoid type conflict with Node.js's Timeout type.
                    window.setTimeout(() => endCall(calleeUsername), 2000);
                }
            }, 30000); // 30 seconds

        } catch (error) {
            console.error('Error starting P2P call:', error);
            setCallStatus(`Error: ${error.message}`);
            setTimeout(() => endCall(calleeUsername), 3000);
        }
    }, [user, createPeerConnection, isCalling, callStatus, endCall, initAudio]);


    const acceptCall = useCallback(async () => {
        if (!incomingCall || !user) return;
        
        stopSound();
        const caller = incomingCall.from;
        
        try {
            setIsCalling(true);
            setCallStatus('Connecting...');
            setCallee(caller);
            
            const stream = await navigator.mediaDevices.getUserMedia({
                video: incomingCall.isVideoCall,
                audio: true,
            });
            setLocalStream(stream);
            setIsVideoEnabled(incomingCall.isVideoCall);

            const peerConnection = createPeerConnection(caller);

            const { data, error } = await supabase.from('webrtc_offers').select('offer').eq('callee', user.username).eq('caller', caller).single();
            if (error || !data) throw new Error('Could not retrieve call offer.');
            
            await peerConnection.setRemoteDescription(new RTCSessionDescription(data.offer));
            const answer = await peerConnection.createAnswer();
            await peerConnection.setLocalDescription(answer);

            const channel = supabase.channel(`user:${caller}`);
            channel.send({
                type: 'broadcast',
                event: 'call-accepted',
                payload: { answer: answer, from: user.username },
            });
            supabase.removeChannel(channel);
            
            setIncomingCall(null);
        } catch (error) {
            console.error("Error accepting call:", error);
            endCall(caller);
        }
    }, [incomingCall, user, createPeerConnection, endCall]);

    const declineCall = useCallback(() => {
        if (!incomingCall || !user) return;
        
        stopSound();
        const channel = supabase.channel(`user:${incomingCall.from}`);
        channel.send({
            type: 'broadcast',
            event: 'call-declined',
            payload: { from: user.username },
        });
        supabase.removeChannel(channel);
        setIncomingCall(null);
    }, [incomingCall, user]);

    useEffect(() => {
        if (!user) {
            if (userChannel.current) {
                supabase.removeChannel(userChannel.current);
                userChannel.current = null;
            }
            return;
        }

        if (!userChannel.current) {
            const channel = supabase.channel(`user:${user.username}`);
            channel.on('broadcast', { event: '*' }, async ({ event, payload }) => {
                switch(event) {
                    case 'incoming-call':
                        setIncomingCall({ from: payload.from, isVideoCall: payload.isVideoCall });
                        await initAudio();
                        playSound('ring', true);
                        break;
                    case 'call-accepted':
                        if (pc.current) {
                            await pc.current.setRemoteDescription(new RTCSessionDescription(payload.answer));
                            setCallStatus('Connecting...');
                            stopSound();
                            if(callTimeoutRef.current) window.clearTimeout(callTimeoutRef.current);
                        }
                        break;
                    case 'call-declined':
                        if (isCalling && callee === payload.from) {
                            playSound('declined', false);
                            setCallStatus('Call Declined');
                            setTimeout(() => endCall(), 2000);
                        }
                        break;
                    case 'call-ended':
                         if (isCalling && callee === payload.from) {
                            endCall();
                        }
                        break;
                    case 'ice-candidate':
                        if (pc.current && payload.candidate) {
                            await pc.current.addIceCandidate(new RTCIceCandidate(payload.candidate));
                        }
                        break;
                }
            }).subscribe();
            userChannel.current = channel;
        }

        return () => {
            // Don't remove channel on unmount, keep it alive
        };
    }, [user, isCalling, callee, endCall, initAudio]);

    const toggleMute = () => {
        if (localStream) {
            localStream.getAudioTracks().forEach(track => {
                track.enabled = !track.enabled;
            });
            setIsMuted(prev => !prev);
        }
    };

    const toggleVideo = () => {
        if (localStream) {
            localStream.getVideoTracks().forEach(track => {
                track.enabled = !track.enabled;
            });
            setIsVideoEnabled(prev => !prev);
        }
    };

    const value = {
        isCalling, callee, callStatus, isMuted, isVideoEnabled,
        incomingCall, localStream, remoteStream, callDuration,
        startP2PCall, acceptCall, declineCall, endCall,
        toggleMute, toggleVideo
    };

    return (
        <CallContext.Provider value={value}>
            {children}
        </CallContext.Provider>
    );
};

export const useCall = (): CallContextType => {
    const context = useContext(CallContext);
    if (context === undefined) {
        throw new Error('useCall must be used within a CallProvider');
    }
    return context;
};
